INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (1,'lets go',' LETS GO là sản phẩm đầu tay của hệ thống học từ vựng VOCABULARY.VN, gồm 7 chủ đề từ vựng tiếng Anh thông dụng được thể hiện qua hình thức Flashcards thông minh hỗ trợ nhớ từ dễ dàng và sâu sắc.  ','Bao gồm 140 từ vựng thuộc 7 chủ đề thông dụng và gần gũi nhất._Khóa học miễn phí cho bạn trải nghiệm cách học từ vựng thông minh cùng VOCA.','Bạn mới bắt đầu học tiếng Anh._Bạn học muốn bổ sung vốn từ vựng giao tiếp thông dụng._Bạn học muốn tìm một phương pháp ghi nhớ từ vựng hiệu quả và nhanh chóng.','Giúp bạn làm quen với cách học từ vựng thông minh cùng VOCA._Giúp bạn tăng khả năng giao tiếp và diễn đạt tiếng Anh trong một số tình huống thông dụng.','asset/img/course_thumb/let-go.jpg',1,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (2,'English Words For Starter (A0)','Khoá học English Words For Starters bao gồm 400 từ vựng tiếng Anh căn bản được sắp xếp theo chủ đề, khóa học phù hợp cho người mới bắt đầu học tiếng Anh hoặc trình độ A0. ','Cung cấp 400 từ vựng cơ bản theo chủ đề._Khóa học đầu tiên trong dòng 6 sản phẩm CEFR (Chứng chỉ châu Âu)._Được xây dựng dựa trên giáo trình Work On Your Vocabulary của NXB Collins.','Bạn học tiếng Anh theo lộ trình từ căn bản đến nâng cao, trình độ mới bắt đầu hoặc mất căn bản._Bạn học muốn xây dựng vốn từ vựng nhưng chưa biết bắt đầu từ đâu._Học sinh, sinh viên muốn cải thiện điểm số tiếng Anh hoặc đáp ứng điều kiện đầu ra của nhà trường.','Giúp bạn xây dựng vốn từ vựng căn bản với 24 chủ đề thông dụng._Rút ngắn 1/4 thời gian học, tạo nền tảng tiếng Anh chỉ trong 15 ngày._Giúp bạn làm quen với từ vựng tiếng Anh theo cách thú vị và dễ dàng nhất.','asset/img/course_thumb/english-words-for-starter.jpg',2,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (3,'English Words For Elementary (A1)','Bộ từ Work On Your VOCABULARYbulary (CEF Level A1) bao gồm 500 từ vựng tiếng Anh dành cho các bạn mới bắt đầu luyện thi chứng chỉ CEFR (Tiếng Anh theo chuẩn Châu Âu), phù hợp cho các bạn ở trình độ Elementary (Sơ cấp). ','Cung cấp 500 từ vựng sơ cấp theo chủ đề._Khóa học thứ 2 trong dòng 6 sản phẩm CEFR (Chứng chỉ châu Âu)._Được xây dựng dựa trên giáo trình Work On Your Vocabulary của NXB Collins.','Bạn học tiếng Anh theo lộ trình từ căn bản đến nâng cao, trình độ cơ bản._Học sinh, sinh viên muốn cải thiện điểm số tiếng Anh hoặc đáp ứng điều kiện đầu ra của nhà trường._Người đi làm cần cải thiện khả năng Anh ngữ để ứng tuyển, phục vụ công việc hoặc thăng tiến.','Giúp bạn tăng vốn từ vựng với 29 chủ đề thông dụng._Tạo nền tảng cho bạn phát triển bốn kỹ năng ở trình độ sơ cấp._Cho bạn tấm “hộ chiếu” chuẩn bị cho các mục tiêu thi cử, lấy chứng chỉ quốc tế hoặc du học.','asset/img/course_thumb/english-words-for-elementary.jpg',2,1);
