--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (7,'English Words For Advanced (C1+2)','Bộ từ Work On Your VOCABULARYbulary (CEF C1 Advanced) bao gồm 540 từ vựng tiếng Anh dành cho các bạn mới bắt đầu luyện thi chứng chỉ CEFR (Tiếng Anh theo chuẩn Châu Âu), phù hợp cho các bạn ở trình độ Cao cấp. ','Cung cấp 500 từ vựng cao trung cấp theo chủ đề._Khóa học thứ 6 trong dòng 6 sản phẩm CEFR (Chứng chỉ châu Âu)._Được xây dựng dựa trên giáo trình Work On Your Vocabulary của NXB Collins.','Bạn học tiếng Anh theo lộ trình từ căn bản đến nâng cao, trình độ trung cấp._Học sinh, sinh viên muốn cải thiện điểm số tiếng Anh hoặc đáp ứng điều kiện đầu ra của nhà trường._Người đi làm cần cải thiện khả năng Anh ngữ để ứng tuyển, phục vụ công việc hoặc thăng tiến.','Giúp bạn tăng vốn từ vựng với 28 chủ đề thông dụng._Tạo nền tảng cho bạn phát triển bốn kỹ năng ở trình độ trung cấp._Cho bạn tấm “hộ chiếu” chuẩn bị cho các mục tiêu thi cử, lấy chứng chỉ quốc tế hoặc du học.','asset/img/course_thumb/english-words-for-advanced.jpg',2,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (8,'3000 SMART WORDS','Bao gồm 3000 từ vựng tiếng Anh thông dụng nhất trong giao tiếp. Bộ từ nhắm đến đối tượng những người học tiếng Anh giao tiếp, người mới bắt đầu học tiếng Anh hoặc người mất căn bản. ','Cung cấp 3.000 từ vựng thông dụng nhất trong giao tiếp với 150 chủ đề._Giúp bạn ghi nhớ từ vựng hiệu quả thông qua phương pháp flashcard cùng hình ảnh – âm thanh sinh động.','Bạn học muốn xây dựng vốn từ vựng nhưng chưa biết bắt đầu từ đâu._Bạn học muốn cải thiện vốn từ vựng để giao tiếp hiệu quả trong đời sống hằng ngày._Bạn học muốn tìm một phương pháp ghi nhớ từ vựng hiệu quả và nhanh chóng.','Giúp bạn hiểu rõ 95% mọi ngữ cảnh của tiếng Anh trong cuộc sống._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong đa dạng các tình huống._Giúp bạn rút ngắn thời gian học tối ưu chỉ với 1/4 thời gian so với những cách học thông thường.','asset/img/course_thumb/3000-smart-words.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (9,'1000 ESSENTIAL WORDS in STORIES (Beginner)','1000 Essential Words in Stories (Beginner) với 60 chủ đề, cung cấp gần 1200 từ vựng tiếng Anh thông dụng trong giao tiếp. Bộ sản phẩm nhắm đến đối tượng mới bắt đầu học tiếng Anh.  ','Cung cấp hơn 1.000 từ vựng giao tiếp thông dụng, trình độ sơ cấp._60 chủ đề là những câu chuyện thú vị, dễ nhớ._Là khóa học đầu tiên thuộc dòng sản phẩm dựa trên giáo trình của chuyên gia ngôn ngữ ứng dụng Paul Nation.','Bạn học muốn xây dựng vốn từ vựng nhưng chưa biết bắt đầu từ đâu._Bạn học muốn cải thiện vốn từ vựng để giao tiếp hiệu quả trong đời sống hằng ngày._Bạn học muốn tìm một phương pháp ghi nhớ từ vựng hiệu quả và thú vị.','Giúp bạn hiểu rõ 95% mọi ngữ cảnh của tiếng Anh trong cuộc sống._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong đa dạng các tình huống._Đồng thời giúp bạn phát triển kỹ năng đọc và nghe hiệu quả. ','asset/img/course_thumb/1000-essential-words-in-stories-beginer-avatar.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (10,'1000 ESSENTIAL WORDS in STORIES (Improver)','1000 Essential Words in Stories (Improver) với 60 chủ đề, cung cấp hơn 1100 từ vựng tiếng Anh thông dụng trong giao tiếp. Bộ sản phẩm nhắm đến đối tượng có trình độ học trung cấp trở lên.  ','Cung cấp hơn 1.000 từ vựng giao tiếp thông dụng, trình độ trung cấp._60 chủ đề là những câu chuyện thú vị, dễ nhớ._Là khóa học thứ 2 thuộc dòng sản phẩm dựa trên giáo trình của chuyên gia ngôn ngữ ứng dụng Paul Nation.','Bạn học muốn xây dựng vốn từ vựng nhưng chưa biết bắt đầu từ đâu._Bạn học muốn cải thiện vốn từ vựng để giao tiếp hiệu quả trong đời sống hằng ngày._Bạn học muốn tìm một phương pháp ghi nhớ từ vựng hiệu quả và thú vị.','Giúp bạn hiểu rõ 95% mọi ngữ cảnh của tiếng Anh trong cuộc sống._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong đa dạng các tình huống._Đồng thời giúp bạn phát triển kỹ năng đọc và nghe hiệu quả. ','asset/img/course_thumb/1000-essential-words-in-stories-improver-avatar.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (11,'1000 ESSENTIAL WORDS in STORIES (Advanced)','1000 Essential Words in Stories (Advanced) với 60 chủ đề, cung cấp hơn 1100 từ vựng tiếng Anh thông dụng trong giao tiếp. Bộ sản phẩm nhắm đến đối tượng học tiếng Anh nâng cao.  ','Cung cấp hơn 1.000 từ vựng giao tiếp thông dụng, trình độ nâng cao._60 chủ đề là những câu chuyện thú vị, dễ nhớ._Là khóa học thứ 3 thuộc dòng sản phẩm dựa trên giáo trình của chuyên gia ngôn ngữ ứng dụng Paul Nation.','Bạn học muốn xây dựng vốn từ vựng nhưng chưa biết bắt đầu từ đâu._Bạn học muốn cải thiện vốn từ vựng để giao tiếp hiệu quả trong đời sống hằng ngày._Bạn học muốn tìm một phương pháp ghi nhớ từ vựng hiệu quả và thú vị.','Giúp bạn hiểu rõ 95% mọi ngữ cảnh của tiếng Anh trong cuộc sống._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong đa dạng các tình huống._Đồng thời giúp bạn phát triển kỹ năng đọc và nghe hiệu quả. ','asset/img/course_thumb/1000-essential-words-in-stories-advanced-avatar.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (12,'EFFORTLESS ENGLISH','Với 3000 từ vựng, cụm từ, thành ngữ tiếng Anh thường gặp nhất trong giao tiếp, được xây dựng dựa trên giáo trình Effortless English của Tiến sỹ AJ Hoge, sẽ giúp bạn củng cố vốn từ giao tiếp một cách nhanh chóng và hữu dụng nhất. ','Cung cấp 3.000 từ vựng giao tiếp thông dụng._Xây dựng dựa trên giáo trình Effortless English (Luyện nói tiếng Anh như người Bản ngữ) của tác giả AJ Hoge._Học từ vựng thông qua những câu chuyện thú vị và bài học bổ ích.','Bạn học muốn cải thiện vốn từ vựng để giao tiếp hiệu quả trong đời sống hằng ngày._Bạn học muốn tìm một phương pháp học từ vựng thú vị qua những câu chuyện._Bạn học đang tìm kiếm hoặc theo phương pháp học tiếng Anh tự nhiên như người bản xứ.','Giúp bạn tăng vốn từ vựng qua 187 câu chuyện và bài học ý nghĩa._Đồng thời giúp bạn phát triển kỹ năng đọc và nghe qua câu chuyện._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong đa dạng các tình huống.','asset/img/course_thumb/Effortless English-04-1-1516866198.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (13,'POWER ENGLISH','Giúp bạn thuộc nằm lòng 1200 từ vựng, cụm từ và thành ngữ quan trọng nhất của giáo trình Power Effortless English của tiến sĩ AJ Hoge trong vòng 30 ngày. ','Cung cấp 1.200 từ vựng tiếng Anh thông dụng theo chủ đề._Thuộc dòng sản phẩm dựa trên giáo trình Effortless English (Luyện nói tiếng Anh như người Bản ngữ) của tác giả AJ Hoge._Học từ vựng thông qua những bài học tạo động lực và tư duy tích cực.','Bạn học muốn cải thiện vốn từ vựng để giao tiếp hiệu quả trong đời sống hằng ngày._Bạn học muốn tìm một phương pháp học từ vựng thú vị qua những câu chuyện._Bạn học đang tìm kiếm hoặc theo phương pháp học tiếng Anh tự nhiên như người bản xứ.','Giúp bạn tăng vốn từ vựng qua 60 bài học truyền cảm hứng._Đồng thời giúp bạn phát triển kỹ năng đọc và nghe qua câu chuyện._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong đa dạng các tình huống.','asset/img/course_thumb/power-english-1515085618.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (14,'LEARN REAL ENGLISH','Effortless English II với hơn 750 từ, cụm từ, thành ngữ thường xuyên xuất hiện trong giao tiếp hàng ngày của người Mỹ, thuộc giáo trình Learn Real English của tiến sĩ AJ Hoge. ','Cung cấp 1.200 từ vựng tiếng Anh thông dụng theo chủ đề._Thuộc dòng sản phẩm dựa trên giáo trình Effortless English (Luyện nói tiếng Anh như người Bản ngữ) của tác giả AJ Hoge._Học từ vựng thông qua những bài học tạo động lực và tư duy tích cực.','Bạn học muốn cải thiện vốn từ vựng để giao tiếp hiệu quả trong đời sống hằng ngày._Bạn học muốn tìm một phương pháp học từ vựng thú vị qua những câu chuyện._Bạn học đang tìm kiếm hoặc theo phương pháp học tiếng Anh tự nhiên như người bản xứ.','Giúp bạn tăng vốn từ vựng qua 60 bài học truyền cảm hứng._Đồng thời giúp bạn phát triển kỹ năng đọc và nghe qua câu chuyện._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong đa dạng các tình huống.','asset/img/course_thumb/learn-real-english-1515124742.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (15,'VOCABULARY for VOA Special English','VOCABULARY for VOA Special English bao gồm 50 chủ đề tương ứng với 50 bản tin VOA ở 5 lĩnh vực nổi bật: Nông nghiệp, Kinh tế, Giáo dục, Sức khỏe, Công nghệ. Cung cấp cho người học gần 900 từ khóa với cách học sinh động, thú vị.  ','Cung cấp 900 từ vựng tiếng Anh thông dụng theo chủ đề._Được xây dựng dựa trên chương trình VOA Special English của Đài Tiếng nói Hoa Kỳ._Học từ vựng thông qua các bản tin thời sự thực tế.','Bạn học tiếng Anh đã lâu, đọc hiểu tốt nhưng không Nghe - Nói được._Bạn học muốn cải thiện vốn từ vựng kết hợp luyện nghe giao tiếp._Bạn học đang tìm kiếm một giáo trình thực tế với kiến thức phải thông dụng.','Giúp bạn tăng vốn từ vựng qua 50 bản tin cùng 5 lĩnh vực chủ đề kinh tế, giáo dục, chính trị, công nghệ, nông nghiệp._Đồng thời giúp bạn phát triển kỹ năng đọc và nghe hiệu quả._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong các tình huống thực tiễn.','asset/img/course_thumb/voa-xanh.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (16,'VOCABULARY FOR ENGLISH AT WORK','Bao gồm 1200 từ vựng tiếng Anh chia làm 67 chủ đề tương ứng với 67 tình huống tiếng Anh được VOCABULARY thiết kế dành riêng cho các Anh/Chị đang làm việc tại văn phòng.  ','Cung cấp 900 từ vựng tiếng Anh thông dụng theo chủ đề._Được xây dựng dựa trên chương trình VOA Special English của Đài Tiếng nói Hoa Kỳ._Học từ vựng thông qua các bản tin thời sự thực tế.','Bạn học tiếng Anh đã lâu, đọc hiểu tốt nhưng không Nghe - Nói được._Bạn học muốn cải thiện vốn từ vựng kết hợp luyện nghe giao tiếp._Bạn học đang tìm kiếm một giáo trình thực tế với kiến thức phải thông dụng.','Giúp bạn tăng vốn từ vựng qua 50 bản tin cùng 5 lĩnh vực chủ đề kinh tế, giáo dục, chính trị, công nghệ, nông nghiệp._Đồng thời giúp bạn phát triển kỹ năng đọc và nghe hiệu quả._Giúp bạn tăng khả năng giao tiếp và diễn đạt bằng tiếng Anh trong các tình huống thực tiễn.','asset/img/course_thumb/english-at-work-1540885524.jpg',3,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (17,'VOCABULARY FOR TOEIC (NEW)','VOCABULARY for TOEIC bao gồm 1100 từ vựng thông dụng nhất trong kỳ thi TOEIC, được chọn lọc và soạn thảo kĩ càng từ các nguồn uy tín, giúp người học nhớ từ vựng sâu sắc, hiệu quả và dễ dàng chinh phục mục tiêu TOEIC đề ra trong thời gian ngắn. ','Cung cấp 1.100 từ vựng tiếng Anh thông dụng._Bao gồm 55 chủ đề thường gặp nhất trong bài thi TOEIC._Được xây dựng kết hợp nhiều giáo trình nổi tiếng như Target TOEIC, Economy TOEIC,…','Bạn học đang luyện thi chứng chỉ tiếng Anh TOEIC._Bạn học muốn phát triển tiếng Anh giao tiếp ở các lĩnh vực kinh tế, tài chính và văn phòng._Người đi làm cần cải thiện khả năng Anh ngữ để ứng tuyển, phục vụ công việc hoặc thăng tiến.','Giúp bạn tăng trung bình 25% - 35% điểm thi hiện tại và đạt kết quả tốt trong kỳ thi TOEIC._Giúp bạn cải thiện khả năng giao tiếp và diễn đạt bằng tiếng Anh._Giúp bạn rút ngắn thời gian học tối ưu chỉ với 1/4 thời gian so với những cách học thông thường.','asset/img/course_thumb/toeic.jpg',4,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (18,'VOCABULARY for TOEFL (NEW)','VOCABULARY for TOEFL (NEW) bao gồm 1000 từ vựng thông dụng nhất trong kỳ thi TOEFL được chọn lọc và soạn thảo kĩ càng từ các nguồn uy tín (Cambrigde, Collins, Oxford,..), giúp người học dễ dàng chinh phục mục tiêu chỉ trong một thời gian ngắn.  ','Cung cấp 1.000 từ vựng tiếng Anh thông dụng._Bao gồm 50 chủ đề thường gặp nhất trong bài thi TOEFL._Kết hợp các nguồn tài liệu uy tín từ Cambridge, Collins, Oxford,… ','Bạn học mong muốn tham gia kỳ thi chứng chỉ TOEFL._Bạn học luyện thi TOEFL đã lâu nhưng vẫn chưa đạt mục tiêu mong muốn._Bạn học muốn xây dựng vốn từ vựng tiếng Anh học thuật.  ','Giúp bạn tăng điểm số và đạt kết quả tốt trong kỳ thi TOEFL._Giúp bạn rút ngắn thời gian học tối ưu chỉ với 1/4 thời gian so với những cách học thông thường.','asset/img/course_thumb/toefl.jpg',4,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (19,'VOCABULARY for IELTS','VOCABULARY for IELTS bao gồm 1000 từ vựng thông dụng nhất trong kỳ thi IELTS được chọn lọc và soạn thảo kĩ càng từ các nguồn uy tín (Cambrigde, Collins, Oxford,..), giúp người học dễ dàng chinh phục mục tiêu chỉ trong một thời gian ngắn.  ','Cung cấp 2.000 từ vựng tiếng Anh thông dụng._Bao gồm 50 chủ đề thường gặp nhất trong bài thi IELTS._Kết hợp các nguồn tài liệu uy tín từ Cambridge, Collins, Oxford,… ','Bạn học mong muốn tham gia kỳ thi chứng chỉ IELTS._Bạn học luyện thi IELTS đã lâu nhưng khó lên band và đạt mục tiêu mong muốn._Bạn học muốn xây dựng vốn từ vựng tiếng Anh học thuật.  ','Giúp bạn tăng band điểm trung bình và đạt kết quả tốt trong kỳ thi IELTS._Giúp bạn rút ngắn thời gian học tối ưu chỉ với 1/4 thời gian so với những cách học thông thường.','asset/img/course_thumb/ielts.jpg',4,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (20,'KEY WORDS FOR IELTS (Starter)','Bộ Key Words For IELTS (Starter) gồm 900 từ vựng tiếng Anh thường gặp nhất trong bài thi IELTS, phù hợp cho đối tượng cho trình độ tiếng Anh từ Sơ cấp trở lên, với mục tiêu IELTS từ 4.0 đến 5.5 điểm. ','Cung cấp 900 từ vựng tiếng Anh cơ bản thường gặp trong bài thi IELTS._Là khóa học đầu tiên thuộc dòng sản phẩm Key Words for IELTS của NXB Collins COBUILD.','Bạn học mong muốn tham gia kỳ thi chứng chỉ IELTS nhưng chưa biết bắt đầu từ đâu._Bạn học luyện thi IELTS đã lâu nhưng khó lên band và cần lấy lại căn bản._Bạn học muốn xây dựng vốn từ vựng tiếng Anh học thuật. ','Giúp bạn xây dựng nhanh vốn từ vựng cơ bản, từ band 4.0 đến 5.5+._Giúp bạn tăng band điểm trung bình và đạt kết quả tốt trong kỳ thi IELTS._Giúp bạn rút ngắn thời gian học tối ưu chỉ với 1/4 thời gian so với những cách học thông thường.','asset/img/course_thumb/Key-Words-For-IELTS-starter.jpg',4,1);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (59,'OTHER WAYS TO SAY: DEVELOPING 1','Bộ câu gồm những cách nói khác nhau để thể hiện hoặc trình bày một nội dung, giúp ngôn ngữ của bạn phong phú và đa dạng hơn. ','Cung cấp 100 mẫu câu tiếng Anh thường gặp nhất trong quá trình phỏng vấn xin việc. _Đi kèm 100 cặp câu hỏi đáp giúp bạn chuyển bị cho buổi phỏng vấn._Nội dung thiết kế dựa trên các giáo trình uy tín được đội ngũ giáo viên VOCA biên soạn.','Bạn học có dự định đi phỏng vấn hoặc vào làm ở các công ty nước ngoài._Người đi làm cần cải thiện khả năng Anh ngữ để ứng tuyển, phục vụ công việc hoặc thăng tiến.','Giúp bạn chuẩn bị hành trang ngôn ngữ khi đi phỏng vấn hoặc làm việc._Giúp bạn tăng khả năng giao tiếp và diễn đạt tiếng Anh._Giúp bạn cải thiện kỹ năng xử lý và tư duy khéo léo trong các tình huống thường gặp khi phỏng vấn.','asset/img/course_thumb/other-way-001-1648537527.jpg',9,2);
