--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (63,'ENGLISH SENTENCES FOR IDIOMS AND EXPRESSIONS 1','Khoá học English Sentences for Idioms and Expressions 1 sẽ cung cấp cho bạn học 150 thành ngữ và tục ngữ tiếng Anh thông dụng, được truyền tải dễ hiểu qua phương pháp học Câu với VOCABULARY. ','Cung cấp 150 mẫu câu với thành ngữ tiếng Anh phổ biến nhất trong giao tiếp. _Là khóa học đầu tiên trong dòng sản phẩm 300 Mẫu câu Thành ngữ tiếng Anh._Xây dựng dựa trên các giáo trình uy tín của Longman, Oxford và Cambridge.','Bạn học muốn nâng cấp trình độ tiếng Anh giao tiếp._Bạn học ôn thi các chứng chỉ muốn đạt điểm tốt ở phần thi Nghe-nói/Vấn đáp._Người đi làm cần cải thiện khả năng Anh ngữ để ứng tuyển, phục vụ công việc hoặc thăng tiến.','Giúp bạn nâng cao khả năng giao tiếp và ứng dụng ngay tiếng Anh vào cuộc sống hằng ngày._Giúp bạn tăng khả năng diễn đạt bằng tiếng Anh với thành ngữ trong 30 chủ đề và tình huống thông dụng._Giúp bạn tự tin giao tiếp, sử dụng ngôn ngữ tự nhiên như người bản xứ.','asset/img/course_thumb/english-sentences-for-idioms-and-expressions-no1-1622541776.jpg',9,2);
