--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (65,'SENTENCES FOR TOEIC PART 2','Khóa học với 160 mẫu câu hỏi thường gặp nhất trong phần II của bài thi TOEIC mới nhất cùng câu trả lời, giúp bạn làm quen và chuẩn bị cho kỳ thi. ','Cung cấp 160 mẫu câu hỏi thường gặp nhất trong Part II thuộc phần thi Nghe (Listening) đề thi TOEIC. _Đi kèm 160 câu trả lời và ngữ cảnh tương ứng được dùng trên thực tế cũng như xuất hiện trong bài thi._Nội dung thiết kế dựa trên các giáo trình uy tín của Viện Khảo thí giáo dục Hoa Kỳ (ETS).','Bạn học đang luyện thi chứng chỉ tiếng Anh TOEIC._Bạn học muốn phát triển tiếng Anh giao tiếp ở các lĩnh vực kinh tế, tài chính và văn phòng._Người đi làm cần cải thiện khả năng Anh ngữ để ứng tuyển, phục vụ công việc hoặc thăng tiến.','Giúp bạn làm quen và đạt điểm cao trong nhóm bài thứ hai thuộc phần thi Nghe của đề thi TOEIC._Giúp bạn tăng trung bình điểm thi hiện tại và đạt kết quả tốt trong kỳ thi TOEIC._Giúp bạn hình thành khả năng giao tiếp và ứng dụng ngay tiếng Anh vào cuộc sống hằng ngày. ','asset/img/course_thumb/sentences-for-toeic-part-2-1650010397.jpg',10,2);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (66,'SENTENCES FOR TOEIC PART 3','Khóa học với 260 mẫu câu hỏi thường gặp nhất trong phần III của bài thi TOEIC mới nhất cùng câu trả lời, giúp bạn làm quen và chuẩn bị cho kỳ thi. ','Cung cấp 160 mẫu câu hỏi thường gặp nhất trong Part II thuộc phần thi Nghe (Listening) đề thi TOEIC. _Đi kèm 160 câu trả lời và ngữ cảnh tương ứng được dùng trên thực tế cũng như xuất hiện trong bài thi._Nội dung thiết kế dựa trên các giáo trình uy tín của Viện Khảo thí giáo dục Hoa Kỳ (ETS).','Bạn học đang luyện thi chứng chỉ tiếng Anh TOEIC._Bạn học muốn phát triển tiếng Anh giao tiếp ở các lĩnh vực kinh tế, tài chính và văn phòng._Người đi làm cần cải thiện khả năng Anh ngữ để ứng tuyển, phục vụ công việc hoặc thăng tiến.','Giúp bạn làm quen và đạt điểm cao trong nhóm bài thứ hai thuộc phần thi Nghe của đề thi TOEIC._Giúp bạn tăng trung bình điểm thi hiện tại và đạt kết quả tốt trong kỳ thi TOEIC._Giúp bạn hình thành khả năng giao tiếp và ứng dụng ngay tiếng Anh vào cuộc sống hằng ngày. ','asset/img/course_thumb/sentence-toeic-part-3-1653387401.jpg',10,2);
--INSERT INTO course (id,title, description,content,targetLearner,goal,thumbnail,id_group,id_category) VALUES (67,'SENTENCES FOR ENGLISH GRADE 3','Sentences For English Grade 3 sẽ cung cấp cho bạn hơn 140 câu tiếng Anh thông dụng dành riêng cho đối tượng là học sinh lớp 3. ','Cung cấp 140 mẫu câu tiếng Anh dành cho học sinh lớp 3._Xây dựng dựa theo chương trình sách giáo khoa mới (thí điểm) của Bộ GD-ĐT kết hợp cùng NXB Pearson.','Học sinh lớp 3 muốn cải thiện điểm số tiếng Anh trên trường lớp._Học sinh muốn tìm một phương pháp ghi nhớ từ vựng hiệu quả và nhanh chóng._Bố mẹ đang tìm một phương pháp giúp con học tiếng Anh.','Giúp bạn xây dựng vốn từ vựng bám sát chương trình học._Giúp bạn cải thiện điểm số và thành tích học tập trên lớp._Giúp bạn rút ngắn thời gian học tối ưu chỉ với 1/4 thời gian so với những cách học thông thường.','asset/img/course_thumb/sentences-for-english-grade-3-1629799938.jpg',11,2);
